// src/components/Map.js
import React, { useEffect, useState } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const Map = ({ zoneId }) => {
  const [mapData, setMapData] = useState(null);

  useEffect(() => {
    if (zoneId) {
      // Fetch map data based on selected zoneId (this assumes an endpoint exists for that)
      fetch(`/get_map_data/${zoneId}`)
        .then((response) => response.json())
        .then((data) => setMapData(data))
        .catch((error) => console.error('Error fetching map data:', error));
    }
  }, [zoneId]);

  useEffect(() => {
    if (mapData) {
      const map = L.map("map").setView([51.505, -0.09], 13); // Replace with appropriate coordinates

      L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
        attribution:
          '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      L.imageOverlay(mapData.imageUrl, mapData.bounds).addTo(map);  // Use image URL and bounds from `mapData`

      return () => {
        map.remove();
      };
    }
  }, [mapData]);

  return <div id="map" style={{ height: "500px" }}></div>;
};

export default Map;
