import React, { useState, useEffect } from "react";
import { MapContainer, ImageOverlay, FeatureGroup } from "react-leaflet";
import { EditControl } from "react-leaflet-draw";

const ZoneBuilder = () => {
    const [maps, setMaps] = useState([]);
    const [selectedMap, setSelectedMap] = useState(null);
    const [imageSrc, setImageSrc] = useState(null);
    const [zones, setZones] = useState([]);

    useEffect(() => {
        fetchMaps();
    }, []);

    const fetchMaps = async () => {
        try {
            const response = await fetch("/maps/get_maps");
            const data = await response.json();
            if (response.ok) {
                setMaps(data);
            } else {
                throw new Error("Failed to load maps.");
            }
        } catch (error) {
            console.error("Error loading maps:", error);
        }
    };

    const handleMapChange = async (mapId) => {
        setSelectedMap(mapId);
        try {
            const response = await fetch(`/maps/map_image/${mapId}`);
            if (!response.ok) throw new Error("Failed to load image.");
            const blob = await response.blob();
            setImageSrc(URL.createObjectURL(blob));
        } catch (error) {
            console.error("Error loading map image:", error);
        }
    };

    const handleZoneSave = async (zone) => {
        const payload = {
            map_id: selectedMap,
            zone_coordinates: zone.geometry.coordinates[0],
        };

        try {
            const response = await fetch("/api/add_zone", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload),
            });

            if (!response.ok) throw new Error("Failed to save zone.");
            alert("Zone saved successfully!");
            setZones([...zones, zone]);
        } catch (error) {
            console.error("Error saving zone:", error);
            alert("Error saving zone.");
        }
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h2>Zone Builder</h2>
            <label>Select Map:</label>
            <select onChange={(e) => handleMapChange(e.target.value)} value={selectedMap || ""}>
                <option value="">-- Select a Map --</option>
                {maps.map((map) => (
                    <option key={map.i_map} value={map.i_map}>{map.x_nm_map}</option>
                ))}
            </select>

            {imageSrc && (
                <MapContainer center={[0, 0]} zoom={2} style={{ height: "500px", width: "80%", marginTop: "20px" }}>
                    <ImageOverlay url={imageSrc} bounds={[[-100, -100], [100, 100]]} />

                    <FeatureGroup>
                        <EditControl
                            position="topright"
                            draw={{
                                rectangle: false,
                                circle: false,
                                circlemarker: false,
                                marker: false,
                            }}
                            onCreated={(e) => handleZoneSave(e.layer.toGeoJSON())}
                        />
                    </FeatureGroup>
                </MapContainer>
            )}
        </div>
    );
};

export default ZoneBuilder;
