// /home/parcoadmin/parco_fastapi/app/src/components/ZoneBuilder.js
import React, { useState, useEffect } from "react";
import MapZoneBuilder from "./MapZoneBuilder";
import "./ZoneBuilder.css";

const ZoneBuilder = () => {
    const [maps, setMaps] = useState([]);
    const [zones, setZones] = useState([]);
    const [parentZones, setParentZones] = useState([]);
    const [selectedMapId, setSelectedMapId] = useState(null);
    const [selectedZoneType, setSelectedZoneType] = useState(null);
    const [selectedParentZone, setSelectedParentZone] = useState(null);
    const [zoneName, setZoneName] = useState("");
    const [vertices, setVertices] = useState([]); 

    useEffect(() => {
        fetch("/zonebuilder/get_maps")
            .then(response => response.json())
            .then(data => setMaps(data.maps))
            .catch(error => console.error("‚ùå Error fetching maps:", error));

        fetch("/zonebuilder/get_zone_types")
            .then(response => response.json())
            .then(data => setZones(data))
            .catch(error => console.error("‚ùå Error fetching zones:", error));

        fetch("/zonebuilder/get_parent_zones")
            .then(response => response.json())
            .then(data => setParentZones(data.zones))
            .catch(error => console.error("‚ùå Error fetching parent zones:", error));
    }, []);

    const saveZone = async () => {
        if (vertices.length === 0) {
            alert("‚ö†Ô∏è No vertices detected! Please draw a zone before saving.");
            return;
        }

        const zoneData = {
            zone_name: zoneName,
            map_id: selectedMapId,
            zone_level: selectedZoneType,
            parent_zone_id: selectedParentZone || null,
            vertices: vertices,
        };

        console.log("üì° Sending Zone Data:", zoneData);

        try {
            const response = await fetch("/zonebuilder/create_zone", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(zoneData),
            });

            const result = await response.json();
            if (response.ok) {
                alert(`‚úÖ Zone '${zoneName}' created successfully!`);
            } else {
                alert(`‚ùå Error: ${result.detail}`);
            }
        } catch (error) {
            console.error("‚ùå Error creating zone:", error);
            alert("‚ö†Ô∏è Failed to create zone");
        }
    };

    return (
        <div>
            <h2>Zone Builder</h2>
            <label>Zone Name:</label>
            <input 
                type="text" 
                value={zoneName} 
                onChange={(e) => setZoneName(e.target.value)} 
                placeholder="Enter Zone Name" 
            />
            <MapZoneBuilder zoneId={selectedMapId} onDrawComplete={setVertices} />
            <button onClick={saveZone}>Save Zone</button>
        </div>
    );
};

export default ZoneBuilder;
