import React, { useState, useEffect } from "react";

const MapList = () => {
    const [maps, setMaps] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchMaps();
    }, []);

    const fetchMaps = async () => {
        try {
            const response = await fetch("/maps/get_maps");
            const data = await response.json();

            if (response.ok) {
                setMaps(data);
                setLoading(false);
            } else {
                throw new Error(data.detail || "Failed to fetch maps");
            }
        } catch (err) {
            setError(err.message);
            setLoading(false);
        }
    };

    const deleteMap = async (mapId) => {
        if (!window.confirm("Are you sure you want to delete this map?")) return;

        try {
            const response = await fetch(`/maps/delete_map/${mapId}`, {
                method: "DELETE",
            });

            if (response.ok) {
                setMaps(maps.filter(map => map.i_map !== mapId));
                alert("Map deleted successfully!");
            } else {
                const data = await response.json();
                alert(`Error: ${data.detail}`);
            }
        } catch (error) {
            alert("Failed to delete map.");
            console.error("Delete error:", error);
        }
    };

    if (loading) return <p>Loading maps...</p>;
    if (error) return <p style={{ color: "red" }}>‚ùå {error}</p>;

    return (
        <div style={{ maxWidth: "600px", margin: "auto", padding: "20px", border: "1px solid #ddd", borderRadius: "8px" }}>
            <h2>Uploaded Maps</h2>
            {maps.length === 0 ? <p>No maps uploaded yet.</p> : (
                <ul style={{ listStyle: "none", padding: 0 }}>
                    {maps.map(map => (
                        <li key={map.i_map} style={{ padding: "10px", borderBottom: "1px solid #ddd", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                            <span>{map.x_nm_map}</span>
                            <button onClick={() => deleteMap(map.i_map)} style={{ padding: "5px 10px", background: "red", color: "white", border: "none", cursor: "pointer" }}>üóë Delete</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default MapList;
