import React, { useState, useEffect } from "react";
import { Tabs, Tab } from "react-bootstrap";
import Map from "./components/Map";
import CreateTrigger from "./components/CreateTrigger";

const TriggerDemo = () => {
  const [maps, setMaps] = useState([]);
  const [zones, setZones] = useState([]);
  const [triggerDirections, setTriggerDirections] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null);
  const [selectedZone, setSelectedZone] = useState(null);
  const [triggerName, setTriggerName] = useState("");
  const [triggerDirection, setTriggerDirection] = useState("");
  const [topElevation, setTopElevation] = useState(8);
  const [bottomElevation, setBottomElevation] = useState(-1);
  const [triggerColor, setTriggerColor] = useState("Red");
  const [eventList, setEventList] = useState([]);
  const [coordinates, setCoordinates] = useState("");
  const [zoneTypes, setZoneTypes] = useState([]);
  const [parentZones, setParentZones] = useState([]);
  const [selectedZoneLevel, setSelectedZoneLevel] = useState("");
  const [selectedParentZone, setSelectedParentZone] = useState("");
  const [vertices, setVertices] = useState([]);
  const [editedVertices, setEditedVertices] = useState({});

  // Fetch initial data
  useEffect(() => {
    fetch("/maps/get_maps")
      .then((response) => response.json())
      .then((data) => setMaps(data))
      .catch((error) => console.error("Error fetching maps:", error));

    fetch("/api/get_zone_types")
      .then((response) => response.json())
      .then((data) => setZoneTypes(data))
      .catch((error) => console.error("Error fetching zone types:", error));

    fetch("/api/get_parent_zones")
      .then((response) => response.json())
      .then((data) => setParentZones(data.zones))
      .catch((error) => console.error("Error fetching parent zones:", error));

    fetch("/api/list_trigger_directions")
      .then((response) => response.json())
      .then((data) => setTriggerDirections(data))
      .catch((error) => console.error("Error fetching trigger directions:", error));
  }, []);

  // Update zones when a map is selected
  useEffect(() => {
    if (selectedMap) {
      fetch(`/maps/get_campus_zones/${selectedMap.campus_id || selectedMap.map_id}`) // Adjust based on data structure
        .then((response) => response.json())
        .then((data) => {
          setZones(data.zones || []);
          setSelectedZone(null); // Reset selected zone
        })
        .catch((error) => console.error("Error fetching zones:", error));
    }
  }, [selectedMap]);

  // Handle trigger creation
  const handleCreateTrigger = async () => {
    if (!triggerName || !selectedZone || !selectedMap || !selectedZoneLevel) {
      alert("Please fill all required fields (Trigger Name, Zone, Map, Zone Level).");
      return;
    }

    const triggerData = {
      name: triggerName,
      direction: triggerDirection,
      zone_id: selectedZone,
      top_elevation: topElevation,
      bottom_elevation: bottomElevation,
      color: triggerColor,
      zone_level: selectedZoneLevel,
      parent_zone_id: selectedParentZone || null,
      coordinates: coordinates ? JSON.parse(coordinates).map(coord => ({ n_x: coord[0], n_y: coord[1], n_z: 0 })) : null,
    };

    const response = await fetch("/api/add_trigger", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(triggerData),
    });

    if (response.ok) {
      const result = await response.json();
      alert(`Trigger created with ID: ${result.trigger_id}`);
      setEventList([...eventList, `${triggerName} created at ${coordinates} with ${triggerDirection} direction`]);
      setCoordinates(""); // Reset coordinates
    } else {
      alert("Error creating trigger");
    }
  };

  // Load vertices for selected zones
  const loadVerticesForSelectedZones = () => {
    const checkedZones = document.querySelectorAll("#zoneList input[type='checkbox']:checked")
      .map(cb => parseInt(cb.value))
      .filter(id => !isNaN(id));
    if (checkedZones.length === 0) {
      alert("Please select at least one zone to load vertices.");
      return;
    }
    const filteredVertices = vertices.filter(v => checkedZones.includes(parseInt(v.zone_id)));
    setEditedVertices(filteredVertices.reduce((acc, v) => ({ ...acc, [v.vertex_id]: v }), {}));
  };

  // Handle vertex changes
  const handleVertexChange = (vertexId, field, value) => {
    setEditedVertices(prev => ({
      ...prev,
      [vertexId]: { ...prev[vertexId], [field]: parseFloat(value) }
    }));
  };

  // Save all edited vertices
  const saveAllVertices = async () => {
    const updatedVertices = Object.values(editedVertices).map(v => ({
      vertex_id: v.vertex_id,
      x: v.x,
      y: v.y,
      z: v.z || 0
    }));
    const response = await fetch("/api/update_vertices", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ vertices: updatedVertices }),
    });
    if (response.ok) {
      alert("Vertices updated successfully!");
      // Refresh vertices
      if (selectedZone) {
        fetch(`/api/get_zone_vertices/${selectedZone}`)
          .then((response) => response.json())
          .then((data) => setVertices(data.vertices))
          .catch((error) => console.error("Error fetching vertices:", error));
      }
    } else {
      alert("Error saving vertices");
    }
  };

  return (
    <div>
      <h1>ParcoRTLS Trigger Management</h1>
      <Tabs defaultActiveKey="mapAndTrigger" id="trigger-demo-tabs">
        <Tab eventKey="mapAndTrigger" title="Map & Trigger">
          <div className="trigger-map-section">
            <div className="trigger-form">
              <h3>Create Trigger</h3>
              <input
                type="text"
                placeholder="Trigger Name"
                value={triggerName}
                onChange={(e) => setTriggerName(e.target.value)}
              />
              <select value={selectedZoneLevel} onChange={(e) => setSelectedZoneLevel(e.target.value)}>
                <option value="">Select Zone Level</option>
                {zoneTypes.map((zt) => (
                  <option key={zt.zone_level} value={zt.zone_level} data-zone-level={zt.zone_level}>
                    {zt.zone_name}
                  </option>
                ))}
              </select>
              <select value={selectedMap ? selectedMap.map_id : ""} onChange={(e) => {
                const map = maps.find(m => m.map_id === parseInt(e.target.value));
                setSelectedMap(map);
              }}>
                <option value="">Select Map</option>
                {maps.map((map) => (
                  <option key={map.map_id} value={map.map_id}>
                    {map.name}
                  </option>
                ))}
              </select>
              <select value={selectedParentZone} onChange={(e) => setSelectedParentZone(e.target.value)}>
                <option value="">None</option>
                {parentZones.map((pz) => (
                  <option key={pz.zone_id} value={pz.zone_id}>{pz.name}</option>
                ))}
              </select>
              <select value={triggerDirection} onChange={(e) => setTriggerDirection(e.target.value)}>
                <option value="">Select Direction</option>
                {triggerDirections.map((direction) => (
                  <option key={direction.i_dir} value={direction.x_dir}>
                    {direction.x_dir}
                  </option>
                ))}
              </select>
              <input
                type="number"
                placeholder="Top Elevation"
                value={topElevation}
                onChange={(e) => setTopElevation(Number(e.target.value))}
              />
              <input
                type="number"
                placeholder="Bottom Elevation"
                value={bottomElevation}
                onChange={(e) => setBottomElevation(Number(e.target.value))}
              />
              <select value={triggerColor} onChange={(e) => setTriggerColor(e.target.value)}>
                <option value="Red">Red</option>
                <option value="Blue">Blue</option>
                <option value="Green">Green</option>
              </select>
              <button onClick={handleCreateTrigger}>Create Trigger</button>
            </div>
            <div className="map-selection">
              <h3>Select a Zone</h3>
              <div id="zoneList">
                {zones.map((zone) => (
                  <div key={zone.zone_id}>
                    <input
                      type="checkbox"
                      value={zone.zone_id}
                      checked={selectedZone === zone.zone_id}
                      onChange={(e) => setSelectedZone(e.target.value)}
                    />
                    <span>{zone.zone_name}</span>
                  </div>
                ))}
              </div>
              {selectedMap && (
                <Map
                  zoneId={selectedZone || selectedMap.map_id}
                  onDrawComplete={(coords) => setCoordinates(coords)}
                />
              )}
            </div>
            <h3>Edit Trigger Vertices</h3>
            <table id="vertexTable">
              <thead>
                <tr>
                  <th>Vertex #</th>
                  <th>X Coordinate</th>
                  <th>Y Coordinate</th>
                  <th>Z Coordinate</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {Object.values(editedVertices).map((v, i) => (
                  <tr key={v.vertex_id}>
                    <td>{i + 1}</td>
                    <td><input type="number" value={v.x || 0} onChange={(e) => handleVertexChange(v.vertex_id, "x", e.target.value)} /></td>
                    <td><input type="number" value={v.y || 0} onChange={(e) => handleVertexChange(v.vertex_id, "y", e.target.value)} /></td>
                    <td><input type="number" value={v.z || 0} onChange={(e) => handleVertexChange(v.vertex_id, "z", e.target.value)} /></td>
                    <td><button>Save</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button onClick={loadVerticesForSelectedZones}>Load Vertices for Selected Zones</button>
            <button onClick={saveAllVertices}>Save All Changes</button>
          </div>
        </Tab>
      </Tabs>
    </div>
  );
};

export default TriggerDemo;
