// # VERSION 250316 /home/parcoadmin/parco_fastapi/app/src/components/BuildOutTool.js 0P.10B.01
// #  
// # ParcoRTLS Middletier Services, ParcoRTLS DLL, ParcoDatabases, ParcoMessaging, and other code
// # Copyright (C) 1999 - 2025 Affiliated Commercial Services Inc.
// # Invented by Scott Cohen & Bertrand Dugal.
// # Coded by Jesse Chunn O.B.M.'24 and Michael Farnsworth and Others
// # Published at GitHub https://github.com/scocoh/IPS-RTLS-UWB
// #
// # Licensed under AGPL-3.0: https://www.gnu.org/licenses/agpl-3.0.en.html


import React, { useState, useEffect } from "react";
import MapBuildOut from "./MapBuildOut";

const BuildOutTool = () => {
    const [devices, setDevices] = useState([]);
    const [deviceTypes, setDeviceTypes] = useState([]);
    const [deviceId, setDeviceId] = useState("");
    const [deviceName, setDeviceName] = useState("");
    const [deviceType, setDeviceType] = useState("");
    const [location, setLocation] = useState({ x: null, y: null, z: 0 });
    const [editingDevice, setEditingDevice] = useState(null);

    // Fetch initial data
    useEffect(() => {
        fetch("/api/get_all_devices")
            .then(res => res.json())
            .then(data => setDevices(data))
            .catch(err => console.error("Error fetching devices:", err));

        fetch("/api/list_device_types")
            .then(res => res.json())
            .then(data => setDeviceTypes(data))
            .catch(err => console.error("Error fetching device types:", err));
    }, []);

    const handleAddDevice = async () => {
        const formData = new FormData();
        formData.append("device_id", deviceId);
        formData.append("device_type", deviceType);
        formData.append("device_name", deviceName);
        if (location.x !== null) formData.append("n_moe_x", location.x);
        if (location.y !== null) formData.append("n_moe_y", location.y);
        formData.append("n_moe_z", location.z);

        try {
            const res = await fetch("/api/add_device", {
                method: "POST",
                body: formData,
            });
            const result = await res.json();
            if (res.ok) {
                setDevices([...devices, { x_id_dev: deviceId, i_typ_dev: parseInt(deviceType), x_nm_dev: deviceName, n_moe_x: location.x, n_moe_y: location.y, n_moe_z: location.z }]);
                resetForm();
            } else {
                alert(`Error: ${result.detail}`);
            }
        } catch (err) {
            console.error("Error adding device:", err);
        }
    };

    const handleEditDevice = async () => {
        const formData = new FormData();
        formData.append("device_name", deviceName);
        if (location.x !== null) formData.append("n_moe_x", location.x);
        if (location.y !== null) formData.append("n_moe_y", location.y);
        formData.append("n_moe_z", location.z);

        try {
            const res = await fetch(`/api/edit_device/${editingDevice.x_id_dev}`, {
                method: "PUT",
                body: formData,
            });
            const result = await res.json();
            if (res.ok) {
                setDevices(devices.map(d => d.x_id_dev === editingDevice.x_id_dev ? { ...d, x_nm_dev: deviceName, n_moe_x: location.x, n_moe_y: location.y, n_moe_z: location.z } : d));
                resetForm();
            } else {
                alert(`Error: ${result.detail}`);
            }
        } catch (err) {
            console.error("Error editing device:", err);
        }
    };

    const handleDeleteDevice = async (deviceId) => {
        try {
            const res = await fetch(`/api/delete_device/${deviceId}`, { method: "DELETE" });
            const result = await res.json();
            if (res.ok) {
                setDevices(devices.filter(d => d.x_id_dev !== deviceId));
            } else {
                alert(`Error: ${result.detail}`);
            }
        } catch (err) {
            console.error("Error deleting device:", err);
        }
    };

    const resetForm = () => {
        setDeviceId("");
        setDeviceName("");
        setDeviceType("");
        setLocation({ x: null, y: null, z: 0 });
        setEditingDevice(null);
    };

    const handleMapClick = (coords) => {
        setLocation({ x: coords.n_x, y: coords.n_y, z: 0 });
    };

    return (
        <div style={{ padding: "20px" }}>
            <h2>Build Out Tool</h2>
            <div style={{ marginBottom: "20px" }}>
                <label>Device ID: </label>
                <input 
                    value={deviceId} 
                    onChange={(e) => setDeviceId(e.target.value)} 
                    disabled={editingDevice !== null} 
                    style={{ marginRight: "10px" }}
                />
                <label>Device Name: </label>
                <input 
                    value={deviceName} 
                    onChange={(e) => setDeviceName(e.target.value)} 
                    style={{ marginRight: "10px" }}
                />
                <label>Device Type: </label>
                <select 
                    value={deviceType} 
                    onChange={(e) => setDeviceType(e.target.value)} 
                    style={{ marginRight: "10px" }}
                >
                    <option value="">Select Type</option>
                    {deviceTypes.map(t => (
                        <option key={t.i_typ_dev} value={t.i_typ_dev}>{t.x_dsc_dev}</option>
                    ))}
                </select>
                <button 
                    onClick={editingDevice ? handleEditDevice : handleAddDevice}
                    style={{ padding: "5px 10px" }}
                >
                    {editingDevice ? "Update" : "Add"}
                </button>
                {editingDevice && (
                    <button 
                        onClick={resetForm} 
                        style={{ padding: "5px 10px", marginLeft: "10px" }}
                    >
                        Cancel
                    </button>
                )}
            </div>
            <div style={{ marginBottom: "20px" }}>
                <p>Location: X={location.x || "N/A"}, Y={location.y || "N/A"}, Z={location.z}</p>
                <MapBuildOut onDrawComplete={handleMapClick} devices={devices} />
            </div>
            <h3>Devices</h3>
            <ul style={{ listStyle: "none", padding: 0 }}>
                {devices.map(d => (
                    <li key={d.x_id_dev} style={{ marginBottom: "10px" }}>
                        {d.x_id_dev} - {d.x_nm_dev} (Type: {d.i_typ_dev}, Loc: {d.n_moe_x || "N/A"}, {d.n_moe_y || "N/A"}, {d.n_moe_z || "N/A"})
                        <button 
                            onClick={() => { 
                                setEditingDevice(d); 
                                setDeviceId(d.x_id_dev); 
                                setDeviceName(d.x_nm_dev); 
                                setDeviceType(d.i_typ_dev); 
                                setLocation({ x: d.n_moe_x, y: d.n_moe_y, z: d.n_moe_z || 0 }); 
                            }} 
                            style={{ marginLeft: "10px", padding: "5px" }}
                        >
                            Edit
                        </button>
                        <button 
                            onClick={() => handleDeleteDevice(d.x_id_dev)} 
                            style={{ marginLeft: "10px", padding: "5px" }}
                        >
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BuildOutTool;
