///home/parcoadmin/parco_fastapi/app/src/components/MapZoneBuilder.js
import React, { useEffect, useRef, useState, memo } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Leaflet core CSS
import "leaflet-draw/dist/leaflet.draw.css"; // Leaflet.Draw CSS
import "leaflet-draw"; // Import Leaflet.Draw
import "./Map.css"; // Canvas styling

const MapZoneBuilder = memo(({ zoneId, onDrawComplete, triggerColor, useLeaflet }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const drawnItems = useRef(new L.FeatureGroup());
  const canvasRef = useRef(null);
  const [mapData, setMapData] = useState(null);
  const [error, setError] = useState(null);
  const isInitialized = useRef(false);
  const points = useRef([]); // Stores drawn zone points
  const isDrawing = useRef(false);
  const ctxRef = useRef(null);
  const imageRef = useRef(null);
  const lastClickTime = useRef(0);

  // âœ… Fetch Map Data from FastAPI (ZoneBuilder API)
  useEffect(() => {
    if (zoneId) {
      const fetchMapData = async () => {
        try {
          const response = await fetch(`/zonebuilder/get_map_data/${zoneId}`);
          if (!response.ok) {
            const text = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, response: ${text}`);
          }
          const data = await response.json();
          console.log("âœ… Map data fetched (ZoneBuilder):", data);
          setMapData(data);
          setError(null);
        } catch (error) {
          console.error("âŒ Error fetching map data:", error);
          setError(`Error fetching map data: ${error.message}`);
        }
      };
      fetchMapData();
    }
  }, [zoneId]);

  // ðŸ–Œ **Canvas Rendering Logic**
  useEffect(() => {
    if (mapData && canvasRef.current && !useLeaflet && !isInitialized.current) {
      console.log("ðŸ–Œ Restoring Canvas rendering...");
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      ctxRef.current = ctx;

      canvas.width = 600;
      canvas.height = 500;

      const img = new Image();
      img.crossOrigin = "anonymous";
      img.src = mapData.imageUrl;
      imageRef.current = img;

      img.onload = () => {
        console.log("âœ… Canvas: Map image loaded successfully", mapData.imageUrl);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
      };

      img.onerror = () => {
        console.error("âŒ Failed to load map image:", mapData.imageUrl);
        setError("Failed to load map image. Please check the server response.");
      };

      // ðŸ–± **Canvas Click to Add Zone**
      const handleMouseDown = (e) => {
        if (e.button === 0) {
          isDrawing.current = true;
          const rect = canvas.getBoundingClientRect();
          const scaleX = canvas.width / rect.width;
          const scaleY = canvas.height / rect.height;
          const x = (e.clientX - rect.left) * scaleX;
          const y = (e.clientY - rect.top) * scaleY;

          points.current.push({ x, y });
          console.log("ðŸ–± Click registered at:", { x, y });

          drawPolygon(points.current);
        }
      };

      const drawPolygon = (points) => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        if (points.length === 0) return;
        ctx.beginPath();
        ctx.moveTo(points[0].x, points[0].y);
        points.forEach((point) => ctx.lineTo(point.x, point.y));
        ctx.closePath();
        ctx.strokeStyle = triggerColor || "red";
        ctx.stroke();
      };

      canvas.addEventListener("mousedown", handleMouseDown);
      return () => {
        canvas.removeEventListener("mousedown", handleMouseDown);
      };
    }
  }, [mapData, useLeaflet]);

  // ðŸ—º **Leaflet Rendering Logic**
  useEffect(() => {
    if (mapData && mapRef.current && useLeaflet && !isInitialized.current) {
      console.log("ðŸ—º Restoring Leaflet rendering...");

      if (!mapInstance.current) {
        mapInstance.current = L.map(mapRef.current, {
          crs: L.CRS.Simple,
          minZoom: -5,
          maxZoom: 5,
          zoomControl: true,
          attributionControl: false,
        });
      }

      // Remove existing overlays to prevent duplicates
      mapInstance.current.eachLayer(layer => {
        if (layer instanceof L.ImageOverlay) {
          mapInstance.current.removeLayer(layer);
        }
      });

      const bounds = [
        [mapData.bounds[0][0], mapData.bounds[0][1]], // Bottom-left
        [mapData.bounds[1][0], mapData.bounds[1][1]]  // Top-right
      ];

      const overlay = L.imageOverlay(mapData.imageUrl, bounds).addTo(mapInstance.current);
      mapInstance.current.fitBounds(bounds);
      isInitialized.current = true;
      console.log("âœ… Leaflet Map Loaded:", mapData.imageUrl);

      // ðŸ–± **Leaflet Draw Tool for Adding Zones**
      mapInstance.current.addLayer(drawnItems.current);
      const drawControl = new L.Control.Draw({
        edit: { featureGroup: drawnItems.current },
        draw: {
          polygon: { shapeOptions: { color: triggerColor || "red", weight: 2 } },
          rectangle: false,
          circle: false,
          polyline: false,
          marker: false,
        },
      });
      mapInstance.current.addControl(drawControl);

      mapInstance.current.on(L.Draw.Event.CREATED, (event) => {
        const layer = event.layer;
        drawnItems.current.addLayer(layer);
        const coords = layer.getLatLngs()[0].map((latLng, index) => ({
          n_x: latLng.lng,
          n_y: latLng.lat,
          n_z: 0,
          n_ord: index + 1
        }));

        console.log("ðŸ–Œ Scaled points (Leaflet):", coords);
        if (onDrawComplete) onDrawComplete(coords);
      });
    }
  }, [mapData, useLeaflet]);

  // ðŸ§¹ Cleanup when switching maps
  useEffect(() => {
    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
        isInitialized.current = false;
      }
      if (mapData && imageRef.current) {
        imageRef.current.src = ""; // Clear image source to prevent memory leaks
      }
    };
  }, [mapData]);

  // **Return the UI**
  if (useLeaflet) {
    return (
      <div>
        {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
        <div ref={mapRef} style={{ height: "500px", width: "600px", border: "2px solid black" }} />
      </div>
    );
  } else {
    return (
      <div>
        {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
        <canvas ref={canvasRef} id="map" style={{ border: "2px solid black" }} />
      </div>
    );
  }
});

export default MapZoneBuilder;
