import React, { useEffect, useRef, useState, memo } from "react";
import "./Map.css"; // Ensure this matches the file name (Map.css)

const Map = memo(({ zoneId, onDrawComplete, triggerColor }) => {
  const canvasRef = useRef(null);
  const [mapData, setMapData] = useState(null);
  const [error, setError] = useState(null);
  const points = useRef([]);
  const isDrawing = useRef(false);
  const ctxRef = useRef(null);
  const imageRef = useRef(null);

  // Debug mount/unmount
  useEffect(() => {
    console.log("Map component mounted with zoneId:", zoneId);
    return () => {
      console.log("Map component unmounted with zoneId:", zoneId);
    };
  }, [zoneId]);

  // Fetch map data when zoneId changes
  useEffect(() => {
    if (zoneId && !mapData) {
      const fetchMapData = async () => {
        try {
          const response = await fetch(`/maps/get_map_data/${zoneId}`);
          if (!response.ok) {
            const text = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, response: ${text}`);
          }
          const data = await response.json();
          console.log("Map data fetched:", data);
          setMapData(data);
          setError(null);
        } catch (error) {
          console.error("Error fetching map data:", error);
          setError(`Error fetching map data: ${error.message}`);
        }
      };

      fetchMapData();
    }
  }, [zoneId, mapData]);

  // Initialize the canvas and draw the map image
  useEffect(() => {
    if (mapData && canvasRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      ctxRef.current = ctx;

      // Set canvas dimensions to match the container
      canvas.width = 600;
      canvas.height = 500;

      // Load the map image
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.src = mapData.imageUrl;
      imageRef.current = img;

      img.onload = () => {
        console.log("Map image loaded successfully:", mapData.imageUrl, "Dimensions:", img.width, "x", img.height);
        // Draw the image scaled to the canvas
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

        // Initialize drawing logic
        const getCanvasCoordinates = (e) => {
          const rect = canvas.getBoundingClientRect();
          const x = e.clientX - rect.left;
          const y = e.clientY - rect.top;
          return { x, y };
        };

        const drawPolygon = (points, currentPoint = null, close = false) => {
          ctx.clearRect(0, 0, canvas.width, canvas.height);
          // Redraw the map image
          ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

          // Draw the polygon
          if (points.length === 0) return;
          ctx.beginPath();
          ctx.moveTo(points[0].x, points[0].y);
          for (let i = 1; i < points.length; i++) {
            ctx.lineTo(points[i].x, points[i].y);
          }
          if (currentPoint) {
            ctx.lineTo(currentPoint.x, currentPoint.y);
          }
          if (close) {
            ctx.closePath();
          }
          ctx.strokeStyle = triggerColor || "red";
          ctx.stroke();

          // Draw circles at vertex points
          points.forEach((point) => {
            ctx.beginPath();
            ctx.arc(point.x, point.y, 5, 0, Math.PI * 2);
            ctx.fillStyle = triggerColor || "red";
            ctx.fill();
            ctx.closePath();
          });
        };

        const scalePoints = (points) => {
          const canvasWidth = canvas.width; // 600
          const canvasHeight = canvas.height; // 500
          const imageWidth = img.width; // 721
          const imageHeight = img.height; // 601
          const xScale = imageWidth / canvasWidth; // Scaling factor for x
          const yScale = imageHeight / canvasHeight; // Scaling factor for y
          const xMin = -80;
          const xMax = 160;
          const yMin = -40;
          const yMax = 160;
          const xRange = xMax - xMin; // 240
          const yRange = yMax - yMin; // 200

          const scaledPoints = points.map((point, index) => {
            // Convert canvas pixels to image pixels
            const imageX = point.x * xScale;
            const imageY = point.y * yScale;

            // Map to logical coordinates with y-axis flip
            const x = xMin + (imageX / imageWidth) * xRange; // Map to [-80, 160]
            const y = yMax - (imageY / imageHeight) * yRange; // Map to [-40, 160], flipped
            return { n_x: x, n_y: y, n_z: 0, n_ord: index + 1 };
          });
          return scaledPoints;
        };

        // Mouse event listeners
        const handleMouseDown = (e) => {
          if (e.button === 0) { // Left click
            isDrawing.current = true;
            const point = getCanvasCoordinates(e);
            points.current.push(point);
            drawPolygon(points.current);
          }
        };

        const handleMouseMove = (e) => {
          if (isDrawing.current) {
            const point = getCanvasCoordinates(e);
            drawPolygon(points.current, point);
          }
        };

        const handleMouseUp = (e) => {
          if (e.button === 0) {
            isDrawing.current = false;
          }
        };

        const handleDoubleClick = () => {
          isDrawing.current = false;
          if (points.current.length >= 3) {
            drawPolygon(points.current, null, true);
            const scaledPoints = scalePoints(points.current);
            console.log("Scaled points:", scaledPoints);
            if (onDrawComplete) {
              onDrawComplete(JSON.stringify(scaledPoints));
            }
            points.current = []; // Reset points after completing the polygon
          }
        };

        canvas.addEventListener("mousedown", handleMouseDown);
        canvas.addEventListener("mousemove", handleMouseMove);
        canvas.addEventListener("mouseup", handleMouseUp);
        canvas.addEventListener("dblclick", handleDoubleClick);

        // Cleanup event listeners on unmount
        return () => {
          canvas.removeEventListener("mousedown", handleMouseDown);
          canvas.removeEventListener("mousemove", handleMouseMove);
          canvas.removeEventListener("mouseup", handleMouseUp);
          canvas.removeEventListener("dblclick", handleDoubleClick);
        };
      };

      img.onerror = () => {
        console.error("Failed to load map image:", mapData.imageUrl);
        setError("Failed to load map image. Please check the server response.");
      };
    }
  }, [mapData, onDrawComplete, triggerColor]);

  return (
    <div>
      {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
      <canvas ref={canvasRef} id="map" style={{ border: "2px solid black" }} />
    </div>
  );
});

export default Map;
