/// /home/parcoadmin/parco_fastapi/app/src/components/MapZoneBuilder.js
import React, { useEffect, useRef, useState, memo } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; 
import "leaflet-draw/dist/leaflet.draw.css"; 
import "leaflet-draw"; 
import "./Map.css"; 

const MapZoneBuilder = memo(({ zoneId, onDrawComplete, triggerColor, useLeaflet }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const drawnItems = useRef(new L.FeatureGroup());
  const canvasRef = useRef(null);
  const [mapData, setMapData] = useState(null);
  const [error, setError] = useState(null);
  const isInitialized = useRef(false);
  const points = useRef([]); 
  const isDrawing = useRef(false);
  const ctxRef = useRef(null);
  const imageRef = useRef(null);

  // ✅ Fetch Map Data from FastAPI (Ensures we get correct feet-based scaling)
  useEffect(() => {
    if (zoneId) {
      const fetchMapData = async () => {
        try {
          const response = await fetch(`/zonebuilder/get_map_data/${zoneId}`);
          if (!response.ok) {
            const text = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, response: ${text}`);
          }
          const data = await response.json();
          console.log("✅ Map data fetched (ZoneBuilder):", data);
          setMapData(data);
          setError(null);
        } catch (error) {
          console.error("❌ Error fetching map data:", error);
          setError(`Error fetching map data: ${error.message}`);
        }
      };
      fetchMapData();
    }
  }, [zoneId]);

  // 🖌 **Canvas Rendering for Zone Drawing**
  useEffect(() => {
    if (mapData && canvasRef.current && !useLeaflet && !isInitialized.current) {
      console.log("🖌 Restoring Canvas rendering...");
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      ctxRef.current = ctx;

      canvas.width = 600;
      canvas.height = 500;

      const img = new Image();
      img.crossOrigin = "anonymous";
      img.src = mapData.imageUrl;
      imageRef.current = img;

      img.onload = () => {
        console.log("✅ Canvas: Map image loaded successfully", mapData.imageUrl);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
      };

      img.onerror = () => {
        console.error("❌ Failed to load map image:", mapData.imageUrl);
        setError("Failed to load map image. Please check the server response.");
      };

      // 🖱 **Canvas Click to Add Zone**
      const handleMouseDown = (e) => {
        if (e.button === 0) {
          isDrawing.current = true;
          const rect = canvas.getBoundingClientRect();
          const scaleX = (mapData.bounds[1][1] - mapData.bounds[0][1]) / canvas.width;  
          const scaleY = (mapData.bounds[1][0] - mapData.bounds[0][0]) / canvas.height;  

          const x = mapData.bounds[0][1] + ((e.clientX - rect.left) * scaleX);
          const y = mapData.bounds[1][0] - ((e.clientY - rect.top) * scaleY);

          points.current.push({ x, y });
          console.log("🖱 Click registered at (feet):", { x, y });

          drawPolygon(points.current);
        }
      };

      const drawPolygon = (points) => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        if (points.length === 0) return;
        ctx.beginPath();
        ctx.moveTo(points[0].x, points[0].y);
        points.forEach((point) => ctx.lineTo(point.x, point.y));
        ctx.closePath();
        ctx.strokeStyle = triggerColor || "red";
        ctx.stroke();
      };

      canvas.addEventListener("mousedown", handleMouseDown);
      return () => {
        canvas.removeEventListener("mousedown", handleMouseDown);
      };
    }
  }, [mapData, useLeaflet]);

  // 🗺 **Leaflet Rendering for Zones**
  useEffect(() => {
    if (useLeaflet && mapData && mapRef.current && !isInitialized.current) {
      console.log("🗺 Restoring Leaflet rendering...");

      mapInstance.current = L.map(mapRef.current, { crs: L.CRS.Simple })
        .fitBounds(mapData.bounds);

      L.imageOverlay(mapData.imageUrl, mapData.bounds).addTo(mapInstance.current);
      mapInstance.current.addLayer(drawnItems.current);

      const drawControl = new L.Control.Draw({
        edit: { featureGroup: drawnItems.current },
        draw: { 
          polygon: { shapeOptions: { color: triggerColor || "red", weight: 2 } },
          rectangle: false,
          circle: false,
          polyline: false,
          marker: false,
        },
      });
      mapInstance.current.addControl(drawControl);

      mapInstance.current.on(L.Draw.Event.CREATED, (event) => {
        const layer = event.layer;
        drawnItems.current.addLayer(layer);

        // ✅ **Convert Leaflet coordinates (lat/lon) to feet**
        const coords = layer.getLatLngs()[0].map((point, index) => {
          const xFeet = point.lng;  // Already in feet
          const yFeet = point.lat;  // Already in feet
          return { n_x: xFeet, n_y: yFeet, n_z: 0, n_ord: index + 1 };
        });

        console.log("🖌 Scaled points (Leaflet, in feet):", coords);
        if (onDrawComplete) onDrawComplete(coords);
      });

      isInitialized.current = true;
    }
  }, [mapData, useLeaflet]);

  // 🧹 Cleanup on Unmount
  useEffect(() => {
    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
        isInitialized.current = false;
      }
      if (mapData && imageRef.current) {
        imageRef.current.src = ""; 
      }
    };
  }, [mapData]);

  // **Return UI**
  return (
    <div>
      {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
      {useLeaflet ? (
        <div ref={mapRef} style={{ height: "500px", width: "600px", border: "2px solid black" }} />
      ) : (
        <canvas ref={canvasRef} id="map" style={{ border: "2px solid black" }} />
      )}
    </div>
  );
});

export default MapZoneBuilder;
