/// /home/parcoadmin/parco_fastapi/app/src/components/MapZoneBuilder.js
import React, { useEffect, useRef, useState, memo } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-draw/dist/leaflet.draw.css";
import "leaflet-draw";
import "./Map.css"; 

const MapZoneBuilder = memo(({ zoneId, onDrawComplete, triggerColor, useLeaflet }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const drawnItems = useRef(new L.FeatureGroup());
  const canvasRef = useRef(null);
  const [mapData, setMapData] = useState(null);
  const [error, setError] = useState(null);
  const isInitialized = useRef(false);
  const points = useRef([]); 
  const isDrawing = useRef(false);
  const ctxRef = useRef(null);
  const imageRef = useRef(null);

  useEffect(() => {
    if (zoneId) {
      const fetchMapData = async () => {
        try {
          const response = await fetch(`/zonebuilder/get_map_data/${zoneId}`);
          if (!response.ok) {
            const text = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, response: ${text}`);
          }
          const data = await response.json();
          console.log("âœ… Map data fetched (ZoneBuilder):", data);
          setMapData(data);
          setError(null);
        } catch (error) {
          console.error("âŒ Error fetching map data:", error);
          setError(`Error fetching map data: ${error.message}`);
        }
      };
      fetchMapData();
    }
  }, [zoneId]);

  useEffect(() => {
    if (mapData && canvasRef.current && !useLeaflet && !isInitialized.current) {
      console.log("ðŸ–Œ Restoring Canvas rendering...");
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      ctxRef.current = ctx;

      canvas.width = 800;
      canvas.height = 600;

      const img = new Image();
      img.crossOrigin = "anonymous";
      img.src = mapData.imageUrl;
      imageRef.current = img;

      img.onload = () => {
        console.log("âœ… Canvas: Map image loaded successfully", mapData.imageUrl);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
      };

      img.onerror = () => {
        console.error("âŒ Failed to load map image:", mapData.imageUrl);
        setError("Failed to load map image. Please check the server response.");
      };

      const handleMouseDown = (e) => {
        if (e.button === 0) {
          isDrawing.current = true;
          const rect = canvas.getBoundingClientRect();
          const scaleX = canvas.width / (mapData.bounds[1][1] - mapData.bounds[0][1]);  
          const scaleY = canvas.height / (mapData.bounds[1][0] - mapData.bounds[0][0]);  

          const x = mapData.bounds[0][1] + ((e.clientX - rect.left) / scaleX);
          const y = mapData.bounds[1][0] - ((e.clientY - rect.top) / scaleY); 

          points.current.push({ x, y });
          console.log("ðŸ–± Click registered at (feet):", { x, y });

          drawPolygon(points.current);
          onDrawComplete(points.current);  // âœ… Ensure vertices are passed to ZoneBuilder.js
        }
      };

      const drawPolygon = (feetPoints) => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        if (feetPoints.length === 0) return;

        ctx.beginPath();

        feetPoints.forEach((point, index) => {
          const x = (point.x - mapData.bounds[0][1]) * (canvas.width / (mapData.bounds[1][1] - mapData.bounds[0][1]));
          const y = canvas.height - ((point.y - mapData.bounds[0][0]) * (canvas.height / (mapData.bounds[1][0] - mapData.bounds[0][0]))); 

          if (index === 0) {
            ctx.moveTo(x, y);
          } else {
            ctx.lineTo(x, y);
          }

          ctx.fillStyle = "blue";
          ctx.fillRect(x - 2, y - 2, 4, 4);  // âœ… Draw small dots at each vertex
        });

        ctx.closePath();
        ctx.strokeStyle = triggerColor || "red";
        ctx.stroke();
      };

      canvas.addEventListener("mousedown", handleMouseDown);
      return () => {
        canvas.removeEventListener("mousedown", handleMouseDown);
      };
    }
  }, [mapData, useLeaflet]);

  useEffect(() => {
    if (useLeaflet && mapData && mapRef.current && !isInitialized.current) {
      console.log("ðŸ—º Restoring Leaflet rendering...");
      mapInstance.current = L.map(mapRef.current, { crs: L.CRS.Simple })
        .fitBounds(mapData.bounds);

      L.imageOverlay(mapData.imageUrl, mapData.bounds).addTo(mapInstance.current);
      mapInstance.current.addLayer(drawnItems.current);

      const drawControl = new L.Control.Draw({
        edit: { featureGroup: drawnItems.current },
        draw: { 
          polygon: { shapeOptions: { color: triggerColor || "red", weight: 2 } },
          rectangle: false,
          circle: false,
          polyline: false,
          marker: false,
        },
      });
      mapInstance.current.addControl(drawControl);

      mapInstance.current.on(L.Draw.Event.CREATED, (event) => {
        const layer = event.layer;
        drawnItems.current.addLayer(layer);

        const coords = layer.getLatLngs()[0].map((point, index) => ({
          n_x: point.lng,  
          n_y: point.lat,  
          n_z: 0,          
          n_ord: index + 1
        }));

        console.log("ðŸ–Œ Scaled points (Leaflet, in feet):", coords);
        onDrawComplete(coords);  // âœ… Ensure vertices are passed to ZoneBuilder.js
      });

      isInitialized.current = true;
    }
  }, [mapData, useLeaflet]);

  return (
    <div>
      {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
      {useLeaflet ? (
        <div ref={mapRef} style={{ height: "600px", width: "800px", border: "2px solid black" }} />
      ) : (
        <canvas ref={canvasRef} id="map" style={{ border: "2px solid black" }} />
      )}
    </div>
  );
});

export default MapZoneBuilder;
