/// /home/parcoadmin/parco_fastapi/app/src/components/MapZoneBuilder.js
import React, { useEffect, useRef, useState, memo } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-draw/dist/leaflet.draw.css";
import "leaflet-draw";
import "./Map.css";

const MapZoneBuilder = memo(({ zoneId, onDrawComplete, triggerColor, useLeaflet }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const drawnItems = useRef(new L.FeatureGroup());
  const canvasRef = useRef(null);
  const [mapData, setMapData] = useState(null);
  const [error, setError] = useState(null);
  const isInitialized = useRef(false);
  const points = useRef([]);
  const ctxRef = useRef(null);
  const imageRef = useRef(null);

  // âœ… Fetch Map Data from FastAPI
  useEffect(() => {
    if (zoneId) {
      const fetchMapData = async () => {
        try {
          const response = await fetch(`/zonebuilder/get_map_data/${zoneId}`);
          if (!response.ok) {
            const text = await response.text();
            throw new Error(`HTTP error! status: ${response.status}, response: ${text}`);
          }
          const data = await response.json();
          console.log("âœ… Map data fetched (ZoneBuilder):", data);
          setMapData(data);
          setError(null);
        } catch (error) {
          console.error("âŒ Error fetching map data:", error);
          setError(`Error fetching map data: ${error.message}`);
        }
      };
      fetchMapData();
    }
  }, [zoneId]);

  // ðŸ–Œ **Canvas Rendering for Zone Drawing**
  useEffect(() => {
    if (mapData && canvasRef.current && !useLeaflet && !isInitialized.current) {
      console.log("ðŸ–Œ Restoring Canvas rendering...");
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      ctxRef.current = ctx;

      canvas.width = 800;
      canvas.height = 600;

      const img = new Image();
      img.crossOrigin = "anonymous";
      img.src = mapData.imageUrl;
      imageRef.current = img;

      img.onload = () => {
        console.log("âœ… Canvas: Map image loaded successfully", mapData.imageUrl);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
      };

      img.onerror = () => {
        console.error("âŒ Failed to load map image:", mapData.imageUrl);
        setError("Failed to load map image. Please check the server response.");
      };

      const handleMouseDown = (e) => {
        if (e.button === 0) {
          const rect = canvas.getBoundingClientRect();
          const scaleX = canvas.width / (mapData.bounds[1][1] - mapData.bounds[0][1]);
          const scaleY = canvas.height / (mapData.bounds[1][0] - mapData.bounds[0][0]);

          const x = mapData.bounds[0][1] + ((e.clientX - rect.left) / scaleX);
          const y = mapData.bounds[1][0] - ((e.clientY - rect.top) / scaleY);

          points.current.push({ x, y });
          console.log("ðŸ–± Click registered at (feet):", { x, y });

          drawPolygon(points.current);
          onDrawComplete([...points.current]);
        }
      };

      const drawPolygon = (feetPoints) => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        if (feetPoints.length === 0) return;

        ctx.beginPath();

        feetPoints.forEach((point, index) => {
          const x = (point.x - mapData.bounds[0][1]) * scaleX;
          const y = canvas.height - ((point.y - mapData.bounds[0][0]) * scaleY);

          if (index === 0) {
            ctx.moveTo(x, y);
          } else {
            ctx.lineTo(x, y);
          }

          // âœ… Draw small dots at each vertex
          ctx.fillStyle = "red";
          ctx.beginPath();
          ctx.arc(x, y, 3, 0, 2 * Math.PI);
          ctx.fill();
        });

        ctx.closePath();
        ctx.strokeStyle = triggerColor || "red";
        ctx.stroke();
      };

      canvas.addEventListener("mousedown", handleMouseDown);
      return () => {
        canvas.removeEventListener("mousedown", handleMouseDown);
      };
    }
  }, [mapData, useLeaflet]);

  return (
    <div>
      {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
      <canvas ref={canvasRef} id="map" style={{ border: "2px solid black" }} />
    </div>
  );
});

export default MapZoneBuilder;
