import React, { useEffect, useRef, useState } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const MapBuildOut = ({ zoneId, onDrawComplete, devices, useLeaflet }) => {
    const mapRef = useRef(null);
    const mapInstance = useRef(null);
    const canvasRef = useRef(null);
    const [mapData, setMapData] = useState(null);
    const [error, setError] = useState(null);
    const isInitialized = useRef(false);
    const ctxRef = useRef(null);
    const imageRef = useRef(null);
    const canvasBounds = useRef({ x: 0, y: 0, width: 600, height: 400 });

    // Fetch map data
    useEffect(() => {
        if (zoneId) {
            const fetchMapData = async () => {
                try {
                    const response = await fetch(`/zonebuilder/get_map_data/${zoneId}`);
                    if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
                    const data = await response.json();
                    console.log("âœ… Fetched map data:", data);
                    setMapData(data);
                    setError(null);
                } catch (error) {
                    console.error("âŒ Error fetching map data:", error);
                    setError(`Error fetching map data: ${error.message}`);
                }
            };
            fetchMapData();
        }
    }, [zoneId]);

    // Canvas rendering
    useEffect(() => {
        if (!useLeaflet && mapData && canvasRef.current && !isInitialized.current) {
            console.log("ðŸ–Œ Initializing Canvas rendering with mapData:", mapData);
            const canvas = canvasRef.current;
            const ctx = canvas.getContext("2d");
            if (!ctx) return;
            ctxRef.current = ctx;

            canvas.width = 600;
            canvas.height = 400;

            const img = new Image();
            img.crossOrigin = "anonymous";
            img.src = mapData.imageUrl;
            imageRef.current = img;

            img.onload = () => {
                const mapAspect = (mapData.bounds[1][1] - mapData.bounds[0][1]) / (mapData.bounds[1][0] - mapData.bounds[0][0]);
                const canvasAspect = canvas.width / canvas.height;
                let drawWidth = canvas.width;
                let drawHeight = canvas.height;
                let offsetX = 0;
                let offsetY = 0;

                if (mapAspect > canvasAspect) {
                    drawHeight = canvas.width / mapAspect;
                    offsetY = (canvas.height - drawHeight) / 2;
                } else {
                    drawWidth = canvas.height * mapAspect;
                    offsetX = (canvas.width - drawWidth) / 2;
                }

                canvasBounds.current = { x: offsetX, y: offsetY, width: drawWidth, height: drawHeight };
                drawCanvas();
            };

            img.onerror = () => {
                setError("Failed to load map image.");
            };

            const drawCanvas = () => {
                if (!ctxRef.current || !imageRef.current) return;
                const ctx = ctxRef.current;
                ctx.clearRect(0, 0, canvas.width, canvas.height);
                ctx.drawImage(imageRef.current, canvasBounds.current.x, canvasBounds.current.y, canvasBounds.current.width, canvasBounds.current.height);

                const mapWidth = mapData.bounds[1][1] - mapData.bounds[0][1]; // x_max - x_min (feet)
                const mapHeight = mapData.bounds[1][0] - mapData.bounds[0][0]; // y_max - y_min (feet)

                devices.forEach(d => {
                    if (d.n_moe_x && d.n_moe_y) {
                        const xPixel = canvasBounds.current.x + (d.n_moe_x - mapData.bounds[0][1]) * (canvasBounds.current.width / mapWidth);
                        const yPixel = canvasBounds.current.y + (mapData.bounds[1][0] - d.n_moe_y) * (canvasBounds.current.height / mapHeight); // Flip Y
                        ctx.fillStyle = "red";
                        ctx.beginPath();
                        ctx.arc(xPixel, yPixel, 5, 0, 2 * Math.PI);
                        ctx.fill();
                        ctx.fillStyle = "black";
                        ctx.fillText(`${d.x_id_dev}: ${d.x_nm_dev}`, xPixel + 10, yPixel);
                    }
                });

                canvas.onclick = (e) => {
                    const rect = canvas.getBoundingClientRect();
                    const xPixel = e.clientX - rect.left;
                    const yPixel = e.clientY - rect.top;
                    const xFeet = mapData.bounds[0][1] + (xPixel - canvasBounds.current.x) * (mapWidth / canvasBounds.current.width);
                    const yFeet = mapData.bounds[1][0] - (yPixel - canvasBounds.current.y) * (mapHeight / canvasBounds.current.height); // Flip Y
                    onDrawComplete({ n_x: xFeet, n_y: yFeet });
                    ctx.fillStyle = "red";
                    ctx.beginPath();
                    ctx.arc(xPixel, yPixel, 5, 0, 2 * Math.PI);
                    ctx.fill();
                };
            };

            isInitialized.current = true;
            return () => {
                isInitialized.current = false;
            };
        }
    }, [mapData, useLeaflet, devices, onDrawComplete]);

    // Leaflet rendering
    useEffect(() => {
        if (useLeaflet && mapData && mapRef.current && !mapInstance.current) {
            console.log("ðŸ—º Initializing Leaflet with mapData:", mapData);
            mapInstance.current = L.map(mapRef.current, { crs: L.CRS.Simple }).fitBounds(mapData.bounds);
            L.imageOverlay(mapData.imageUrl, mapData.bounds).addTo(mapInstance.current);

            mapInstance.current.on("click", (e) => {
                const { lat, lng } = e.latlng; // lat = y, lng = x in feet
                onDrawComplete({ n_x: lng, n_y: lat });
                L.marker([lat, lng]).addTo(mapInstance.current);
            });

            devices.forEach(d => {
                if (d.n_moe_x && d.n_moe_y) {
                    L.marker([d.n_moe_y, d.n_moe_x])
                        .addTo(mapInstance.current)
                        .bindPopup(`${d.x_id_dev}: ${d.x_nm_dev}`);
                }
            });
        }

        return () => {
            if (mapInstance.current) {
                mapInstance.current.remove();
                mapInstance.current = null;
            }
        };
    }, [mapData, useLeaflet, devices, onDrawComplete]);

    return (
        <div>
            {error && <div style={{ color: "red" }}>{error}</div>}
            {useLeaflet ? (
                <div ref={mapRef} style={{ height: "400px", width: "600px", border: "2px solid black" }} />
            ) : (
                <canvas ref={canvasRef} style={{ border: "2px solid black" }} width="600" height="400" />
            )}
        </div>
    );
};

export default MapBuildOut;
